import { Component, HostListener, OnInit } from '@angular/core';
import { UserService } from './services/user.service';
import { Router } from '@angular/router';
import { jwtDecode } from 'jwt-decode';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit{
  title = 'challengeTod_Frontend';

  isloggedIn: boolean = false;
  currentuser: any;
  isSticky : boolean = false;

  constructor(private userService: UserService, private router: Router){}
 @HostListener('window:scroll', ['$event'])
 handleScroll()

 {
  const scrollPosition = window.pageYOffset;
  this.isSticky = scrollPosition > 50;
 }
  ngOnInit(): void {
    this.userService.isLoggedIn.subscribe((loggedIn) => {
      this.isloggedIn = loggedIn;
      if (loggedIn) {
        const jwtstring = localStorage.getItem('myChallengeToken');
        // console.log('Token:', jwtstring);

        if (jwtstring !== null) {
          try {
            const decodedToken: any = jwtDecode(jwtstring);
            // console.log('Decoded Token:', decodedToken);

            if (decodedToken && decodedToken.unique_name) {
              // console.log('User Name:', decodedToken.unique_name);
              this.currentuser = { name: decodedToken.unique_name };
            } else {
              console.error('Invalid JWT structure:', decodedToken);
            }
          } catch (error) {
            console.error('Error decoding JWT:', error);
          }
        } else {
          this.currentuser = null;
        }
      }
    });
  }

  logout()
  {
    this.userService.logout();
  }
}
<nav class="navbar" [ngClass]="{'navbar-sticky': isSticky}">
    <div class="navbar-container">
      <div class="logo">Bucket List </div>
      <div class="menu">
        <ul>
          <li><a routerLink="/">Home</a></li>
          <li><a routerLink="/myDashboard">My Todo List</a></li>
          <li><a routerLink="/about-me">About Me</a></li>
          <li *ngIf="!isloggedIn"><a routerLink="/login">Login</a></li>

        </ul>
      </div>
      <div class="user-info" *ngIf="isloggedIn">
        <span>{{ currentuser.name }}</span>
        <button (click)="logout()"> <i class='bx bx-log-out-circle'></i></button>

      </div>

    </div>
  </nav>





